mapper.insert:

	SELECT SEQ_CONTRACT.NEXTVAL
	FROM dual

INSERT INTO contract(
CONTRACT_NO
, CTT_NAME
, CTT_PRICE
, CTT_START_DT
,
CTT_END_DT
, REQUESTER_INFO
, REQUESTER_ADDRESS
, REQUESTER_ACCOUNT
,
WORKER_INFO
, WORKER_ADDRESS
, WORKER_ACCOUNT
, UNITY_CONTRACT_NO
,
PROPOSAL_NO
)
VALUES (
#{contractNo}
, #{cttName}
, #{cttPrice}
,
#{cttStartDt}
, #{cttEndDt}
, #{requesterInfo}
, #{requesterAddress}
,
#{requesterAccount}
, #{workerInfo}
, #{workerAddress}
, #{workerAccount}
, #{unityContractNo}
, #{proposalNo}
)


	SELECT SEQ_CONTRACTDETAIL.NEXTVAL
	FROM dual

INSERT INTO contract_detail(
CONTRACT_DETAIL_NO
, CONTRACT_NO
, PRICE
,
PAYMENT_DT
, HISTORY
, ROUND
)
VALUES (#{contractDetailNo}
, #{contractNo}
, #{price}
, #{paymentDt}
, #{history}
, #{round}
)


	SELECT SEQ_SIGNS.NEXTVAL
	FROM dual

INSERT INTO signs
(
SIGNS_NO
, CONTRACT_NO
, USERS_NO
, SIGNS_CONTENT
, SIGNS_DT
)
VALUES(
#{signsNo}
, #{contractNo}
, #{usersNo}
, #{signsContent}
, sysdate
)

mapper.update:
UPDATE contract
SET
CTT_NAME = #{cttName}
, CTT_PRICE = #{cttPrice}
, CTT_START_DT =
#{cttStartDt}
, CTT_END_DT = #{cttEndDt}
, REQUESTER_ADDRESS =
#{requesterAddress}
, REQUESTER_ACCOUNT = #{requesterAccount}
,
WORKER_ADDRESS = #{workerAddress}
, WORKER_ACCOUNT = #{workerAccount}
WHERE contract_no = #{contractNo}

mapper.delete:
DELETE FROM
signs
WHERE contract_no = #{contractNo} and users_no = #{usersNo}
