mapper.insert:

	SELECT SEQ_PROPOSAL.NEXTVAL
	FROM dual

INSERT INTO proposal(
proposal_no
, request_no
, ctt_start_dt
, ctt_end_dt
, write_dt
, worker_postcode
, worker_address
, worker_detail_address
, comments
, file_id
, worker
, requester
, update_dt
)
VALUES(
#{proposalNo}
, #{requestNo}
, #{cttStartDt}
, #{cttEndDt}
, sysdate
, #{workerPostcode}
, #{workerAddress}
, #{workerDetailAddress}
, #{comments}

, #{fileId}

, #{worker}
, #{requester}
, sysdate
)


	SELECT SEQ_PROPOSALDETAIL.NEXTVAL
	FROM dual

INSERT INTO proposal_detail(
proposal_detail_no
, proposal_no
, product
, unit
, qty
, unitprice
, comments
, division
)
VALUES(
#{proposalDetailNo}
, #{proposalNo}
, #{product}
, #{unit}
, #{qty}
, #{unitprice}
, #{comments}
, #{division}
)

mapper.update:

UPDATE proposal
SET
ctt_start_dt = #{cttStartDt}
, ctt_end_dt = #{cttEndDt}
, worker_postcode =  #{workerPostcode}
, worker_address = #{workerAddress}
, worker_detail_address = #{workerDetailAddress}
, comments = #{comments}
, update_dt = sysdate
WHERE proposal_no = #{proposalNo}
