mapper.insert:


	SELECT seq_request.NEXTVAL
	FROM dual			

INSERT INTO request
	(
		request_no
		,requester
		,ctt_place
		,ctt_place_area
		,ctt_place_situation
		,hope_ctt_budget
		,hope_ctt_start_dt
		,hope_ctt_end_dt
		,postcode
		,address
		,detail_address
		,title
		,content
		,request_state
		,region_code
		,category_code

	)
VALUES
	(
		#{requestNo}	
		,#{requester}
		,#{cttPlace}
		,#{cttPlaceArea}
		,#{cttPlaceSituation}
		,#{hopeCttBudget}
		,#{hopeCttStartDt}
		,#{hopeCttEndDt}
		,#{postcode}
		,#{address}
		,#{detailAddress}
		,#{title}
		,#{content}
		,'S01'
		,REGION(#{postcode})
		,#{categoryCode}		
	)

mapper.update:
UPDATE request
SET
	category_code = #{categoryCode}
	,hope_ctt_start_dt = #{hopeCttStartDt}
	,hope_ctt_end_dt = #{hopeCttEndDt}
	,hope_ctt_budget = #{hopeCttBudget}
	,ctt_place = #{cttPlace}
	,ctt_place_situation = #{cttPlaceSituation}
	,ctt_place_area = #{cttPlaceArea}
	,content = #{content}
	,postcode = #{postcode} 
WHERE request_no = #{requestNo}

UPDATE request
SET request_state = 'S07'
WHERE request_no = #{requestNo}
