mapper.insert:

	SELECT SEQ_CTTREPORT.NEXTVAL
	FROM dual

INSERT INTO CTT_REPORT(
CTT_REPORT_NO
, TITLE
, DETAIL_CONTENT
, CTT_DIVISION
, CONTRACT_NO
)VALUES(
#{cttReportNo}
, #{title}
, #{detailContent}
, #{cttDivision}
, #{contractNo}
)

mapper.update:
UPDATE CTT_REPORT
SET
	TITLE = #{title}
	, DETAIL_CONTENT = #{detailContent}
	, CTT_DIVISION = #{cttDivision}
	, CTT_REPORT_DT	= sysdate
WHERE CTT_REPORT_NO = #{cttReportNo}
