mapper.select:
SELECT t.team_no ,t.work_title ,t.work_content ,USERNAME(t.writer) as writer_name ,t.write_dt ,t.contract_no ,FIND_COMMONCODE(r.region_code) as region_code ,r.request_no
FROM team t JOIN contract c ON c.contract_no = t.contract_no JOIN proposal p ON p.proposal_no = c.proposal_no JOIN request r ON r.request_no = p.request_no
WHERE t.team_no = #{teamNo}

SELECT FIND_COMMONCODE(work_code) as work_code ,team_no ,headcount ,hire_complete_tf
FROM team_work_category
WHERE team_no = #{teamNo}
