mapper.select:
SELECT COUNT(*)
FROM users
WHERE users_id = #{usersId}

SELECT COUNT(*)
FROM users
WHERE users_email = #{usersEmail}

SELECT COUNT(*)
FROM users
WHERE users_nick = #{usersNick}

mapper.update:
UPDATE users
SET users_nick = #{usersNick},
	users_phone = #{usersPhone},
	users_email = #{usersEmail}
WHERE users_id = #{usersId}

UPDATE users
SET users_pw = #{usersNewPw}
WHERE users_id = #{usersId}

UPDATE users
SET users_state = quit
WHERE users_id = #{usersId}
